mapper.select:
SELECT C.CARDSEQ, C.CARDTITLE, C.CARDCONTENT, U.USERSEQ, C.CARDREADCOUNT, C.CARDDATE, CARDFILENAME, CARDDEL, U.NICKNAME, NVL(F.LIKECOUNT,0) LIKECOUNT,
NVL(T.TAGCOLOR,' ') TAGCOLOR, NVL(T.TAGHOMETYPE,' ') TAGHOMETYPE, NVL(T.TAGSTYLE,' ') TAGSTYLE
FROM USERS U, CARD_COLLECTIONS C, (SELECT CARDSEQ, NVL(COUNT(*), 0) LIKECOUNT FROM CARD_FAVORITE GROUP BY CARDSEQ) F, CARD_TAG T
WHERE U.USERSEQ=C.USERSEQ AND C.CARDSEQ=F.CARDSEQ(+) AND C.CARDSEQ=T.CARDSEQ(+) 
AND TAGCOLOR=#{tagColor} 
AND TAGHOMETYPE=#{tagHometype} 
AND TAGSTYLE=#{tagStyle} 

	 ORDER BY C.CARDDATE DESC
	 ORDER BY C.CARDREADCOUNT DESC
	 ORDER BY F.LIKECOUNT DESC NULLS LAST

SELECT CARDSEQ, CARDTITLE, CARDCONTENT, U.USERSEQ, CARDREADCOUNT, CARDDATE, CARDFILENAME, CARDDEL, U.NICKNAME
FROM USERS U, CARD_COLLECTIONS C
WHERE U.USERSEQ = C.USERSEQ AND CARDSEQ=#{cardSeq}

mapper.insert:
UPDATE CARD_COLLECTIONS
SET CARDREADCOUNT=CARDREADCOUNT+1
WHERE CARDSEQ=#{cardSeq}

mapper.select:
SELECT NVL(COUNT(*), 0)
FROM CARD_FAVORITE
WHERE CARDSEQ=#{cardSeq}

SELECT NVL(COUNT(*), 0)
FROM CARD_FAVORITE
WHERE USERSEQ=#{userSeq} AND CARDSEQ=#{cardSeq}

SELECT CARDREVSEQ, CARDSEQ, NICKNAME, CARDREVCONTENT, CARDREVDATE, CARDREVDEL
FROM (SELECT ROW_NUMBER()OVER(ORDER BY CARDREVSEQ DESC) AS RNUM, CARDREVSEQ, CARDSEQ, NICKNAME, CARDREVCONTENT, CARDREVDATE, CARDREVDEL FROM CARD_REVIEW WHERE CARDSEQ=#{cardSeq} AND CARDREVDEL=0)	
WHERE RNUM>=1+4*#{cardRevPageNum} AND RNUM<=4+4*#{cardRevPageNum}

mapper.update:
UPDATE CARD_REVIEW
SET CARDREVDEL=1
WHERE CARDREVSEQ=#{cardRevSeq}
