mapper.update:
UPDATE CART
SET CARTSTATUS=1
WHERE CARTCLASSIFY=#{cartClassify} AND PRODUCTSEQ=#{productSeq}

UPDATE CART
SET CARTSTATUS=0
WHERE CARTCLASSIFY=#{cartClassify} AND PRODUCTSEQ=#{productSeq}

UPDATE CART
SET QUANTITY=#{quantity}
WHERE CARTCLASSIFY=#{cartClassify} AND PRODUCTSEQ=#{productSeq}

UPDATE USERS
SET DEFAULTADDRESS=#{defaultAddress}
WHERE USERSEQ=#{userSeq}
