mapper.select:
SELECT EMAIL
FROM USERS
WHERE EMAIL=#{email}

SELECT USERSEQ, EMAIL, USERNAME, NICKNAME, PROFILENAME, PHONE, AUTH, USERDATE, MILEAGE, DEFAULTADDRESS
FROM USERS
WHERE EMAIL=#{email} AND PWD=#{pwd}

SELECT USERSEQ, EMAIL, PWD, USERNAME, NICKNAME, PROFILENAME, PHONE, USERDATE, MILEAGE, DEFAULTADDRESS
FROM USERS
WHERE USERSEQ=#{userSeq}

SELECT USERSEQ, EMAIL, PWD, USERNAME, NICKNAME, PROFILENAME, PHONE, USERDATE, MILEAGE, DEFAULTADDRESS
FROM USERS
WHERE EMAIL=#{email}

mapper.update:
UPDATE USERS
SET PWD = #{pwd}
WHERE USERSEQ = #{userSeq}

mapper.select:
SELECT USERSEQ, EMAIL, PWD, USERNAME, NICKNAME, PROFILENAME, PHONE, USERDATE, MILEAGE, DEFAULTADDRESS
FROM USERS
WHERE PHONE=#{phone}

SELECT U.USERSEQ, U.EMAIL, U.USERNAME, U.NICKNAME, U.PROFILENAME, A.ADDRESSNAME, A.ADDRESS, A.RECEIVEUSER, A.RECEIVEPHONE
FROM USERS U , ADDRESS A
WHERE U.USERSEQ = A.USERSEQ AND U.USERSEQ=#{userSeq}
